<?phprequire("common.php");header('Content-Type: text/html; charset=utf-8');echo "<!DOCTYPE html><!--[if IE 9 ]> <html class='ie9'> <![endif]-->";if (isset($_GET["id"])) {    $pasteId = htmlspecialchars($_GET["id"]);    if ($pasteId != "") {        $query = "            SELECT 				content,				password,				title,				username,				timestamp,				id			FROM 				pastes 			WHERE 				`id` 			IN 				($pasteId)        ";        try {            $stmt = $db->prepare($query);            $stmt->execute();            $rows = $stmt->fetchAll();            if ($rows) {                foreach ($rows as $row):                    $GLOBALS['content'] = htmlentities($row['content'], ENT_QUOTES, 'UTF-8');                    $GLOBALS['password'] = htmlentities($row['password'], ENT_QUOTES, 'UTF-8');                    $GLOBALS['title'] = htmlentities($row['title'], ENT_QUOTES, 'UTF-8');                    $GLOBALS['username'] = htmlentities($row['username'], ENT_QUOTES, 'UTF-8');                    $GLOBALS['timestamp'] = time_diff($row['timestamp']);                    $GLOBALS['id'] = $row['id'];                endforeach;?><html>	<head>		<title> <?php echo $GLOBALS['title']; ?> </title>		<link rel="stylesheet" href="http://<?php echo "{$_SERVER['SERVER_NAME']}"; ?>/css/main.css" />		<style type="text/css">		</style>		<?php echo commonJs(); ?>	</head>	<body>		<a href="javascript:hideNotice();">		<div id="notice">		</div>		</a>		<?php include_once ("header.php"); ?>		<div id="maindiv">			<div id="content" class="main"><?php?>					<?php                if ($GLOBALS['password'] == "") {?>					<div id="pastetitle"><?php echo $GLOBALS['title']; ?></div>						<pre id="notepadarea" class="main"><code><?php echo $GLOBALS['content']; ?></code></pre>							<form action="index.php" id="belowpaste">							<input type="submit" value="New paste" />							<span id="pasteData">							<?php                            echo "Pasted by: {$GLOBALS['username']} | pasted {$GLOBALS['timestamp']} | <a href='rawpaste.php?id={$GLOBALS['id']}'>Raw paste</a> | <a href='download.php?id={$GLOBALS['id']}'>Download paste</a>";?>							</span>							</form><?php                } else {                    if (!isset($_POST['password'])) {?>							<div id="passwordarea">								<form action="paste.php?id=<?php echo $pasteId; ?>" method="post" >									Password: <input type="password" name="password" autocomplete="off" value="" />									<input type="submit" value="submit" />								</form>							</div>							<?php                    } else {                        if ($GLOBALS['password'] == $_POST['password']) {?>							<div id="pastetitle"><?php echo $GLOBALS['title']; ?></div>							<pre id="notepadarea"><code><?php echo $GLOBALS['content']; ?></code></pre>								<form action="index.php" id="belowpaste">									<input type="submit" value="New paste" />																<span id="pasteData">								<?php									echo "Pasted by: {$GLOBALS['username']} | pasted {$GLOBALS['timestamp']} | <a href='rawpaste.php?id={$GLOBALS['id']}'>Raw paste</a> | <a href='download.php?id={$GLOBALS['id']}'>Download paste</a>";?>								</span>								</form>							<?php                        } else {                            echo notice("Invalid Password.");?>							<div id="passwordarea">								<form action="paste.php?id=<?php echo $pasteId; ?>" method="post" >									Password: <input type="password" name="password" autocomplete="off" value="" />									<input type="submit" value="submit" />								</form>							</div>							<?php                        }                    }                }            } else {?>						<form name="notice" action="index.php" method="post">						<input type="hidden" name="notice" value="No paste was found with this id, create a new one instead.">						</form>						<script type="text/javascript">						document.notice.submit();						</script>						<?php            }        }        catch (PDOException $ex) {                        die("Failed to run query");        }    }} else {?>		<form name="notice" action="index.php" method="post">		<input type="hidden" name="notice" value="You have to submit a paste.">		</form>		<script type="text/javascript">		document.notice.submit();		</script>	<?php}?>			</div>				<?php include_once ("sidebar.php"); //SIDEBAR ?>				<?php include_once ("footer.php"); //FOOTER ?>		</div>	</body></html>